import { Moon, Sun } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useEffect, useState } from "react";

export default function ThemeToggle() {
  const [isDark, setIsDark] = useState(false);

  useEffect(() => {
    // Check localStorage and system preference on mount
    const stored = localStorage.getItem('theme');
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
    const shouldBeDark = stored === 'dark' || (!stored && prefersDark);

    setIsDark(shouldBeDark);
    if (shouldBeDark) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, []);

  const toggleTheme = () => {
    const newIsDark = !isDark;
    setIsDark(newIsDark);

    if (newIsDark) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  };

  return (
    <Button 
      variant="ghost" 
      size="icon"
      onClick={toggleTheme}
      className="relative"
    >
      {isDark ? (
        <Sun className="h-5 w-5" />
      ) : (
        <Moon className="h-5 w-5" />
      )}
    </Button>
  );
}

client/src/components/UserHeader.tsx.content:
import { useState, useEffect } from "react";
import { Package, Calendar, FileText, LogOut, Menu, X, Bell, Moon, Sun, Globe, Mail } from "lucide-react";
import { useTranslation, type Language, type TranslationKey } from "@/lib/translations";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import NotificationBell from "@/components/NotificationBell";

interface UserHeaderProps {
  userName: string;
  currentView: string;
  onNavigateToInventory: () => void;
  onNavigateToReservations: () => void;
  onNavigateToReports: () => void;
  onLogout: () => void;
  language: Language;
  onLanguageChange: (language: Language) => void;
}

export default function UserHeader({
  userName,
  currentView,
  onNavigateToInventory,
  onNavigateToReservations,
  onNavigateToReports,
  onLogout,
  language,
  onLanguageChange,
}: UserHeaderProps) {
  const t = useTranslation(language);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isDark, setIsDark] = useState(false);

  const { data: permissions = {} } = useQuery({
    queryKey: ['/api/permissions'],
    queryFn: async () => {
      try {
        const response = await fetch('/api/permissions', { credentials: 'include' });
        if (!response.ok) return { show_language_toggle: true };
        return response.json();
      } catch {
        return { show_language_toggle: true };
      }
    },
  });

  const showLanguageToggle = permissions.show_language_toggle !== false;

  useEffect(() => {
    const stored = localStorage.getItem('theme');
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
    const shouldBeDark = stored === 'dark' || (!stored && prefersDark);
    setIsDark(shouldBeDark);
    if (shouldBeDark) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, []);

  const toggleTheme = () => {
    const newIsDark = !isDark;
    setIsDark(newIsDark);
    if (newIsDark) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  };

  const menuItems: { icon: typeof Package; label: TranslationKey; action: () => void; view: string }[] = [
    { icon: Package, label: 'inventory', action: onNavigateToInventory, view: 'inventory' },
    { icon: Calendar, label: 'reservations', action: onNavigateToReservations, view: 'reservations' },
    { icon: FileText, label: 'reports', action: onNavigateToReports, view: 'reports' },
  ];

  const handleNavClick = (action: () => void) => {
    action();
    setIsMobileMenuOpen(false);
  };

  return (
    <header className="bg-gradient-to-r from-[#667eea] to-[#764ba2] text-white sticky top-0 z-50 shadow-lg">
      <div className="max-w-[1400px] mx-auto px-6">
        <div className="flex items-center justify-between py-6">
          {/* Logo, Title and Username - Left */}
          <div className="flex items-center gap-3 min-w-[200px]">
            <div className="w-10 h-10 rounded-lg bg-white/20 backdrop-blur-sm flex items-center justify-center">
              <Package className="w-6 h-6" />
            </div>
            <div className="flex flex-col">
              <span className="font-bold text-2xl tracking-tight" data-testid="text-header-logo">Inventory</span>
              <span className="text-sm font-medium opacity-90" data-testid="text-header-username">
                {userName}
              </span>
            </div>
          </div>

          {/* Navigation Menu - Center */}
          <nav className="hidden lg:flex items-center gap-0" data-testid="nav-desktop">
            {menuItems.map((item) => {
              const IconComponent = item.icon;
              const isActive = currentView === item.view;
              return (
                <button
                  key={item.view}
                  onClick={item.action}
                  className={`relative flex items-center gap-2 px-4 py-2 font-semibold text-[15px] uppercase tracking-tight transition-all duration-300 group ${
                    isActive ? 'text-white' : 'text-gray-200'
                  }`}
                  data-testid={`button-header-nav-${item.view}`}
                >
                  <IconComponent className="w-4 h-4" />
                  <span>{t(item.label)}</span>
                  <span 
                    className={`absolute left-0 bottom-0 h-1 bg-[#9841ff] transition-all duration-300 ${
                      isActive ? 'w-full' : 'w-0 group-hover:w-full'
                    }`}
                  />
                </button>
              );
            })}
          </nav>

          {/* User Controls - Right */}
          <div className="hidden lg:flex items-center gap-2 min-w-[200px] justify-end">
            <NotificationBell language={language} />

            <Button
              variant="ghost"
              size="icon"
              onClick={toggleTheme}
              className="text-white hover:bg-white/10 h-9 w-9"
              data-testid="button-header-theme-toggle"
            >
              {isDark ? <Sun className="w-4 h-4" /> : <Moon className="w-4 h-4" />}
            </Button>

            {showLanguageToggle && (
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button
                    variant="ghost"
                    size="icon"
                    className="text-white hover:bg-white/10 h-9 w-9"
                    data-testid="button-header-language-toggle"
                  >
                    <Globe className="w-4 h-4" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end" className="w-32">
                  <DropdownMenuItem 
                    onClick={() => onLanguageChange('en')}
                    className={language === 'en' ? 'bg-accent' : ''}
                  >
                    English
                  </DropdownMenuItem>
                  <DropdownMenuItem 
                    onClick={() => onLanguageChange('ar')}
                    className={language === 'ar' ? 'bg-accent' : ''}
                  >
                    العربية
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            )}

            <Button
              onClick={onLogout}
              variant="ghost"
              size="icon"
              className="text-white hover:bg-white/10 h-9 w-9"
              data-testid="button-header-logout"
            >
              <LogOut className="w-4 h-4" />
            </Button>
          </div>

          {/* Mobile Menu Toggle */}
          <button
            className="lg:hidden p-2 rounded hover:bg-white/10 transition-all"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            data-testid="button-mobile-menu-toggle"
          >
            {isMobileMenuOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isMobileMenuOpen && (
          <nav className="lg:hidden pb-6 space-y-2" data-testid="nav-mobile">
            {menuItems.map((item) => {
              const IconComponent = item.icon;
              const isActive = currentView === item.view;
              return (
                <button
                  key={item.view}
                  onClick={() => handleNavClick(item.action)}
                  className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg transition-all ${
                    isActive
                      ? 'bg-white text-[#667eea] font-semibold'
                      : 'bg-white/10 hover:bg-white/20'
                  }`}
                  data-testid={`button-mobile-nav-${item.view}`}
                >
                  <IconComponent className="w-5 h-5" />
                  <span>{t(item.label)}</span>
                </button>
              );
            })}

            <div className="border-t border-white/20 pt-4 mt-4 space-y-3">
              <div className="flex items-center justify-end gap-2 px-4">
                <NotificationBell language={language} />
                <Button
                  variant="ghost"
                  size="icon"
                  onClick={toggleTheme}
                  className="text-white hover:bg-white/20"
                  data-testid="button-mobile-theme-toggle"
                >
                  {isDark ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
                </Button>
                {showLanguageToggle && (
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button
                        variant="ghost"
                        size="icon"
                        className="text-white hover:bg-white/20"
                        data-testid="button-mobile-language-toggle"
                      >
                        <Globe className="w-5 h-5" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end" className="w-32">
                      <DropdownMenuItem 
                        onClick={() => onLanguageChange('en')}
                        className={language === 'en' ? 'bg-accent' : ''}
                      >
                        English
                      </DropdownMenuItem>
                      <DropdownMenuItem 
                        onClick={() => onLanguageChange('ar')}
                        className={language === 'ar' ? 'bg-accent' : ''}
                      >
                        العربية
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                )}
              </div>
              <button
                onClick={() => handleNavClick(onLogout)}
                className="w-full flex items-center gap-3 px-4 py-3 rounded-lg bg-red-500/20 hover:bg-red-500/30 transition-all text-red-100"
                data-testid="button-mobile-logout"
              >
                <LogOut className="w-5 h-5" />
                <span>{t('logout')}</span>
              </button>
            </div>
          </nav>
        )}
      </div>
    </header>
  );
}

client/src/pages/UserHome.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Package, ArrowDown, Camera, Mic, Lightbulb, Box, Wrench, HardDrive, Calendar, BarChart3 } from "lucide-react";
import type { Language } from "@/lib/translations";
import { api, type CategoryWithCounts } from "@/lib/api";

import shape1 from "@assets/pixy_design/shapes/1.png";
import shape2 from "@assets/pixy_design/shapes/2.png";
import shape3 from "@assets/pixy_design/shapes/3.png";
import shape4 from "@assets/pixy_design/shapes/4.png";

interface UserHomeProps {
  userName: string;
  userRole: string;
  userId: string;
  currentLanguage: Language;
  onNavigateToInventory: () => void;
  onNavigateToReservations: () => void;
  onNavigateToReports: () => void;
  onSelectCategory: (categoryId: string) => void;
}

const categoryIcons: Record<string, any> = {
  'Cameras': Camera,
  'Audio Equipment': Mic,
  'Lighting': Lightbulb,
  'Grips & Stabilization': Wrench,
  'Storage Devices': HardDrive,
};

export default function UserHome({ 
  userName, 
  userRole, 
  userId,
  currentLanguage, 
  onNavigateToInventory,
  onNavigateToReservations,
  onNavigateToReports,
  onSelectCategory
}: UserHomeProps) {
  const isArabic = currentLanguage === 'ar';

  const { data: categories = [] } = useQuery<CategoryWithCounts[]>({
    queryKey: ['/api/categories', 'equipment'],
    queryFn: () => api.categories.getAll(true)
  });

  const scrollToCategories = () => {
    const categoriesSection = document.getElementById('categories-section');
    if (categoriesSection) {
      categoriesSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const quickActions = [
    {
      title: isArabic ? 'تصفح المخزون' : 'Browse Inventory',
      subtitle: isArabic ? 'جميع المعدات' : 'All Equipment',
      icon: Package,
      onClick: onNavigateToInventory,
      color: 'bg-primary'
    },
    {
      title: isArabic ? 'حجوزاتي' : 'My Reservations',
      subtitle: isArabic ? 'إدارة الحجوزات' : 'Manage Bookings',
      icon: Calendar,
      onClick: onNavigateToReservations,
      color: 'bg-secondary'
    },
    {
      title: isArabic ? 'التقارير' : 'Reports',
      subtitle: isArabic ? 'عرض البيانات' : 'View Analytics',
      icon: BarChart3,
      onClick: onNavigateToReports,
      color: 'bg-primary'
    }
  ];

  return (
    <div className="min-h-screen bg-background">
      <div className="mil-hero-1 mil-up relative min-h-screen flex items-center overflow-visible">
        <div className="container mx-auto px-6 lg:px-12 relative z-10">
          <div className="max-w-3xl" style={{ paddingTop: '60px' }}>
            <div className="mil-mb15">
              <Package className="w-8 h-8 text-secondary" />
            </div>
            <p className="mil-stylized mil-m2 mil-mb60">
              {isArabic ? 'مرحباً' : 'Welcome'},
            </p>
            <p className="mil-stylized mil-a2 mil-mb30" data-testid="text-user-welcome">
              {userName}!
            </p>

            <div className="relative mil-mb30">
              <h1 className="mil-display1 mil-rubber text-foreground" data-testid="text-hero-title">
                {isArabic ? 'المخزون' : 'INVENTORY'}
              </h1>
            </div>

            <h2 className="mil-head2 mil-m2 mil-mb60">
              {isArabic ? 'نظام إدارة الأصول الكامل' : 'Complete asset management system'}
            </h2>

            <div 
              className="inline-flex items-center gap-3 cursor-pointer group"
              onClick={scrollToCategories}
              data-testid="button-scroll-to-categories"
            >
              <div className="relative w-24 h-24 rounded-full border-2 border-primary flex items-center justify-center group-hover:bg-primary/10 transition-all duration-300">
                <div className="absolute inset-0 flex items-center justify-center">
                  <div className="w-10 h-10 rounded-full bg-primary flex items-center justify-center">
                    <ArrowDown className="w-5 h-5 text-primary-foreground animate-bounce" />
                  </div>
                </div>
                <svg className="absolute inset-0 w-full h-full animate-spin-slow" viewBox="0 0 100 100">
                  <defs>
                    <path id="circle" d="M 50, 50 m -37, 0 a 37,37 0 1,1 74,0 a 37,37 0 1,1 -74,0"/>
                  </defs>
                  <text className="fill-current text-xs uppercase tracking-widest text-muted-foreground">
                    <textPath href="#circle">
                      {isArabic ? 'التصنيفات - التصنيفات - التصنيفات -' : 'categories - categories - categories -'}
                    </textPath>
                  </text>
                </svg>
              </div>
            </div>
          </div>
        </div>

        <div className="mil-shapes absolute top-0 right-0 w-1/2 h-full pointer-events-none hidden lg:block">
          <div className="mil-s-1 absolute" style={{ top: '10%', right: '20%' }}>
            <img 
              src={shape1} 
              alt="decorative shape" 
              className="w-32 h-32 animate-float"
            />
          </div>
          <div className="mil-s-2 absolute" style={{ top: '40%', right: '5%' }}>
            <img 
              src={shape2} 
              alt="decorative shape" 
              className="w-40 h-40 animate-float"
              style={{ animationDelay: '1s' }}
            />
          </div>
          <div className="mil-s-3 absolute" style={{ bottom: '20%', right: '30%' }}>
            <img 
              src={shape3} 
              alt="decorative shape" 
              className="w-28 h-28 animate-float"
              style={{ animationDelay: '2s' }}
            />
          </div>
          <div className="mil-s-4 absolute" style={{ top: '25%', right: '45%' }}>
            <img 
              src={shape4} 
              alt="decorative shape" 
              className="w-20 h-20 animate-float"
              style={{ animationDelay: '0.5s' }}
            />
          </div>
        </div>
      </div>

      <div id="categories-section" className="py-24 px-6 lg:px-12">
        <div className="container mx-auto">
          <div className="flex flex-col md:flex-row md:items-end md:justify-between gap-4 mb-12">
            <div>
              <h2 className="mil-head1 mil-up" data-testid="text-categories-title">
                {isArabic ? 'التصنيفات' : 'Categories'} <span className="mil-a2">{isArabic ? 'المتاحة' : 'Available'}</span>
              </h2>
            </div>
            <div>
              <button 
                onClick={onNavigateToInventory}
                className="mil-stylized mil-a2 flex items-center gap-2 hover:gap-3 transition-all"
                data-testid="link-view-all"
              >
                {isArabic ? 'عرض الكل' : 'View all'}
                <span className="text-lg">&rarr;</span>
              </button>
            </div>
          </div>

          <div className="mil-portfolio-grid mb-16" data-testid="categories-grid">
            {categories.slice(0, 6).map((category) => {
              const IconComponent = categoryIcons[category.name] || Box;
              return (
                <div 
                  key={category.id}
                  className="mil-portfolio-item group"
                  onClick={() => onSelectCategory(category.id)}
                  data-testid={`card-category-${category.id}`}
                >
                  <div className="aspect-[4/3] bg-card border border-border rounded-lg overflow-hidden relative">
                    <div className="absolute inset-0 flex flex-col items-center justify-center p-6 text-center">
                      <div className="w-16 h-16 rounded-full bg-primary/10 flex items-center justify-center mb-4 group-hover:bg-primary/20 transition-colors">
                        <IconComponent className="w-8 h-8 text-primary" />
                      </div>
                      <h3 className="mil-head4 mb-2">{category.name}</h3>
                      <p className="mil-text-sm mil-m2">
                        {category.totalItems} {isArabic ? 'عنصر' : 'items'}
                      </p>
                      <p className="mil-text-sm mil-a2">
                        {category.availableItems} {isArabic ? 'متاح' : 'available'}
                      </p>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          <div className="border-t border-border pt-16">
            <p className="mil-stylized mil-m2 mb-8">{isArabic ? 'إجراءات سريعة' : 'Quick Actions'}</p>
            <div className="grid md:grid-cols-3 gap-6">
              {quickActions.map((action, index) => (
                <div 
                  key={index}
                  className="group p-8 rounded-xl bg-card border border-border hover-elevate transition-all duration-300 cursor-pointer"
                  onClick={action.onClick}
                  data-testid={`card-action-${index}`}
                >
                  <div className={`w-14 h-14 ${action.color} rounded-full flex items-center justify-center mb-6`}>
                    <action.icon className="w-7 h-7 text-primary-foreground" />
                  </div>
                  <h3 className="mil-head3 mb-2">{action.title}</h3>
                  <p className="mil-text-md mil-m2">{action.subtitle}</p>
                </div>
              ))}
            </div>
          </div>

          <div className="mt-24 flex flex-col md:flex-row items-center justify-between gap-6 p-8 bg-card rounded-xl border border-border">
            <div>
              <p className="mil-text-md mil-m1 mb-2">
                {isArabic ? 'ابدأ بتصفح المعدات المتاحة الآن!' : 'Start browsing available equipment now!'}
              </p>
              <p className="mil-text-sm mil-m2">
                {categories.length} {isArabic ? 'تصنيف' : 'categories'} | {categories.reduce((acc, c) => acc + c.totalItems, 0)} {isArabic ? 'عنصر' : 'total items'}
              </p>
            </div>
            <button 
              onClick={onNavigateToInventory}
              className="mil-btn mil-a2"
              data-testid="button-browse-inventory"
            >
              {isArabic ? 'تصفح المخزون' : 'Browse Inventory'}
            </button>
          </div>
        </div>
      </div>

      <footer className="py-12 px-6 lg:px-12 border-t border-border">
        <div className="container mx-auto">
          <div className="flex flex-col md:flex-row items-center justify-between gap-4">
            <p className="mil-text-sm mil-m2">
              {isArabic ? '© 2024. جميع الحقوق محفوظة.' : '© 2024. All rights reserved.'}
            </p>
            <p className="mil-text-sm mil-m2">
              {isArabic ? 'نظام إدارة المخزون' : 'Inventory Management System'}
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
}

client/src/index.css.content:
@import url("https://fonts.googleapis.com/css2?family=Anton&family=Outfit:wght@100..900&display=swap");

@tailwind base;
@tailwind components;
@tailwind utilities;

/* PIXY DESIGN SYSTEM - LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);
  --opaque-button-border-intensity: -8;
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  /* Pixy Colors */
  --background: 45 7% 92%;
  --foreground: 228 3% 21%;
  --border: 45 5% 85%;
  --card: 45 10% 95%;
  --card-foreground: 228 3% 21%;
  --card-border: 45 5% 88%;
  --sidebar: 45 7% 90%;
  --sidebar-foreground: 228 3% 21%;
  --sidebar-border: 45 5% 85%;
  --sidebar-primary: 152 56% 51%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 45 7% 85%;
  --sidebar-accent-foreground: 228 3% 21%;
  --sidebar-ring: 152 56% 51%;
  --popover: 45 10% 94%;
  --popover-foreground: 228 3% 21%;
  --popover-border: 45 5% 88%;

  /* Primary: Pixy Green #37CC8D */
  --primary: 152 56% 51%;
  --primary-foreground: 0 0% 100%;

  /* Secondary: Pixy Orange #FEAF62 */
  --secondary: 32 99% 69%;
  --secondary-foreground: 228 3% 21%;

  --muted: 45 5% 80%;
  --muted-foreground: 0 0% 50%;
  --accent: 152 56% 51%;
  --accent-foreground: 0 0% 100%;
  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 98%;
  --input: 45 5% 75%;
  --ring: 152 56% 51%;

  --chart-1: 152 56% 51%;
  --chart-2: 32 99% 69%;
  --chart-3: 197 37% 24%;
  --chart-4: 43 74% 49%;
  --chart-5: 27 87% 67%;

  --font-sans: "Outfit", sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --font-display: "Anton", sans-serif;

  --radius: .5rem;
  --shadow-2xs: 0px 2px 0px 0px hsl(0 0% 0% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(0 0% 0% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 1px 2px -1px hsl(0 0% 0% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 1px 2px -1px hsl(0 0% 0% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 2px 4px -1px hsl(0 0% 0% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 4px 6px -1px hsl(0 0% 0% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(0 0% 0% / 0.00), 0px 8px 10px -1px hsl(0 0% 0% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(0 0% 0% / 0.00);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --primary-border: hsl(var(--primary));
  --secondary-border: hsl(var(--secondary));
  --muted-border: hsl(var(--muted));
  --accent-border: hsl(var(--accent));
  --destructive-border: hsl(var(--destructive));
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);
  --opaque-button-border-intensity: 9;
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 228 5% 12%;
  --foreground: 45 7% 92%;
  --border: 228 5% 20%;
  --card: 228 5% 15%;
  --card-foreground: 45 7% 92%;
  --card-border: 228 5% 18%;
  --sidebar: 228 5% 14%;
  --sidebar-foreground: 45 7% 92%;
  --sidebar-border: 228 5% 18%;
  --sidebar-primary: 152 56% 51%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 228 5% 20%;
  --sidebar-accent-foreground: 45 7% 92%;
  --sidebar-ring: 152 56% 51%;
  --popover: 228 5% 18%;
  --popover-foreground: 45 7% 92%;
  --popover-border: 228 5% 22%;
  --primary: 152 56% 51%;
  --primary-foreground: 0 0% 100%;
  --secondary: 32 99% 69%;
  --secondary-foreground: 228 3% 21%;
  --muted: 228 5% 22%;
  --muted-foreground: 0 0% 60%;
  --accent: 152 56% 51%;
  --accent-foreground: 0 0% 100%;
  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 98%;
  --input: 228 5% 30%;
  --ring: 152 56% 51%;

  --chart-1: 152 56% 60%;
  --chart-2: 32 99% 75%;
  --chart-3: 197 37% 55%;
  --chart-4: 43 74% 65%;
  --chart-5: 27 87% 70%;

  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --primary-border: hsl(var(--primary));
  --secondary-border: hsl(var(--secondary));
  --muted-border: hsl(var(--muted));
  --accent-border: hsl(var(--accent));
  --destructive-border: hsl(var(--destructive));
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    font-family: "Outfit", sans-serif;
    @apply antialiased bg-background text-foreground;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: "Anton", sans-serif;
    @apply text-foreground uppercase tracking-tight;
    font-weight: 400;
  }
}

/* Pixy Typography Classes */
.mil-display1 {
  font-family: "Anton", sans-serif;
  font-size: clamp(3rem, 8.5vw, 10rem);
  line-height: 1;
  text-transform: uppercase;
  letter-spacing: -0.02em;
}

.mil-head1 {
  font-family: "Anton", sans-serif;
  font-size: clamp(2rem, 4vw, 4rem);
  line-height: 1.1;
  text-transform: uppercase;
}

.mil-head2 {
  font-family: "Anton", sans-serif;
  font-size: clamp(1.5rem, 3vw, 2.5rem);
  line-height: 1.2;
  text-transform: uppercase;
}

.mil-head3 {
  font-family: "Anton", sans-serif;
  font-size: clamp(1.25rem, 2vw, 1.75rem);
  line-height: 1.3;
  text-transform: uppercase;
}

.mil-head4 {
  font-family: "Anton", sans-serif;
  font-size: 1.25rem;
  line-height: 1.3;
  text-transform: uppercase;
}

.mil-stylized {
  font-family: "Outfit", sans-serif;
  font-size: 0.875rem;
  font-weight: 300;
  letter-spacing: 0.1em;
  text-transform: uppercase;
}

.mil-text-md {
  font-size: 1rem;
  line-height: 1.6;
  font-weight: 300;
}

.mil-text-sm {
  font-size: 0.875rem;
  line-height: 1.6;
  font-weight: 300;
}

/* Pixy Accent Colors */
.mil-a1 {
  color: hsl(32 99% 69%);
}

.mil-a2 {
  color: hsl(152 56% 51%);
}

.mil-m1 {
  color: hsl(var(--foreground));
}

.mil-m2 {
  color: hsl(var(--muted-foreground));
}

/* Pixy Menu Button */
.mil-menu-btn {
  width: 24px;
  height: 24px;
  position: relative;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
}

.mil-menu-btn span {
  width: 24px;
  height: 2px;
  background-color: hsl(var(--foreground));
  position: relative;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.mil-menu-btn span::before,
.mil-menu-btn span::after {
  content: "";
  position: absolute;
  width: 24px;
  height: 2px;
  background-color: hsl(var(--foreground));
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.mil-menu-btn span::before {
  top: -8px;
}

.mil-menu-btn span::after {
  top: 8px;
}

.mil-menu-btn.active span {
  background-color: transparent;
}

.mil-menu-btn.active span::before {
  top: 0;
  transform: rotate(45deg);
}

.mil-menu-btn.active span::after {
  top: 0;
  transform: rotate(-45deg);
}

/* Pixy Button Styles */
.mil-btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 1rem 2rem;
  font-family: "Outfit", sans-serif;
  font-size: 0.875rem;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  border-radius: 9999px;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
  border: none;
}

.mil-btn.mil-a2 {
  background-color: hsl(152 56% 51%);
  color: white;
}

.mil-btn.mil-a2:hover {
  background-color: hsl(152 56% 45%);
  transform: translateY(-2px);
}

.mil-btn.mil-a1 {
  background-color: hsl(32 99% 69%);
  color: hsl(228 3% 21%);
}

.mil-btn.mil-a1:hover {
  background-color: hsl(32 99% 63%);
  transform: translateY(-2px);
}

/* Pixy Menu Overlay */
.mil-menu-frame {
  position: fixed;
  top: 0;
  right: 0;
  width: 100%;
  max-width: 420px;
  height: 100vh;
  background-color: hsl(var(--card));
  z-index: 9998;
  transform: translateX(100%);
  transition: transform 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  overflow-y: auto;
}

.mil-menu-frame.active {
  transform: translateX(0);
}

.mil-menu-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 9997;
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s ease;
}

.mil-menu-overlay.active {
  opacity: 1;
  visibility: visible;
}

/* Pixy Main Menu */
.mil-main-menu {
  list-style: none;
  padding: 100px 40px 40px;
  margin: 0;
}

.mil-main-menu > li {
  margin-bottom: 1.5rem;
}

.mil-main-menu > li > a {
  font-family: "Anton", sans-serif;
  font-size: 2rem;
  text-transform: uppercase;
  color: hsl(var(--foreground));
  text-decoration: none;
  display: block;
  transition: color 0.3s ease;
  position: relative;
}

.mil-main-menu > li > a:hover {
  color: hsl(152 56% 51%);
}

.mil-main-menu > li > a::after {
  content: "";
  position: absolute;
  bottom: -4px;
  left: 0;
  width: 0;
  height: 2px;
  background-color: hsl(152 56% 51%);
  transition: width 0.3s ease;
}

.mil-main-menu > li > a:hover::after {
  width: 100%;
}

.mil-main-menu > li.active > a {
  color: hsl(152 56% 51%);
}

.mil-main-menu > li.active > a::after {
  width: 100%;
}

/* Pixy Top Panel */
.mil-top-panel {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 9999;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1.5rem 2rem;
  pointer-events: none;
}

.mil-top-panel > * {
  pointer-events: auto;
}

.mil-logo {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  font-family: "Anton", sans-serif;
  font-size: 1.5rem;
  text-transform: uppercase;
  color: hsl(var(--foreground));
  text-decoration: none;
  transition: color 0.3s ease;
}

.mil-logo:hover {
  color: hsl(152 56% 51%);
}

.mil-logo i {
  font-size: 1.5rem;
}

.mil-tp-btn {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background-color: hsl(var(--card));
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.3s ease;
  border: 1px solid hsl(var(--border));
}

.mil-tp-btn:hover {
  background-color: hsl(152 56% 51%);
  color: white;
  border-color: hsl(152 56% 51%);
}

/* Pixy Cards Grid */
.mil-portfolio-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 2rem;
}

.mil-portfolio-item {
  position: relative;
  border-radius: 0.5rem;
  overflow: hidden;
  cursor: pointer;
  transition: transform 0.3s ease;
}

.mil-portfolio-item:hover {
  transform: translateY(-8px);
}

.mil-portfolio-item img {
  width: 100%;
  aspect-ratio: 4/3;
  object-fit: cover;
  transition: transform 0.5s ease;
}

.mil-portfolio-item:hover img {
  transform: scale(1.05);
}

.mil-portfolio-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 1.5rem;
  background: linear-gradient(to top, rgba(0,0,0,0.8), transparent);
  color: white;
}

/* Pixy Scroll Progress */
.mil-progress-track {
  position: fixed;
  right: 2rem;
  top: 50%;
  transform: translateY(-50%);
  width: 3px;
  height: 100px;
  background-color: hsl(var(--border));
  border-radius: 3px;
  z-index: 9990;
}

.mil-progress {
  width: 100%;
  background-color: hsl(152 56% 51%);
  border-radius: 3px;
  transition: height 0.1s ease;
}

/* Pixy Shapes */
.mil-shapes {
  position: absolute;
  top: 0;
  right: 0;
  width: 50%;
  height: 100%;
  pointer-events: none;
}

.mil-shapes img {
  position: absolute;
  animation: float 6s ease-in-out infinite;
}

.mil-s-1 {
  top: 10%;
  right: 20%;
}

.mil-s-2 {
  top: 40%;
  right: 5%;
}

.mil-s-3 {
  bottom: 20%;
  right: 30%;
}

@keyframes float {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-20px);
  }
}

.animate-float {
  animation: float 6s ease-in-out infinite;
}

@keyframes spin-slow {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

.animate-spin-slow {
  animation: spin-slow 20s linear infinite;
}

/* Pixy Social Icons */
.mil-social {
  display: flex;
  gap: 1rem;
  list-style: none;
  padding: 0;
  margin: 0;
}

.mil-social li a {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: hsl(var(--muted));
  display: flex;
  align-items: center;
  justify-content: center;
  color: hsl(var(--foreground));
  transition: all 0.3s ease;
}

.mil-social li a:hover {
  background-color: hsl(152 56% 51%);
  color: white;
}

/* Utility Classes */
.mil-up {
  opacity: 1;
  transform: translateY(0);
  transition: opacity 0.6s ease, transform 0.6s ease;
}

.mil-mb15 { margin-bottom: 0.9375rem; }
.mil-mb30 { margin-bottom: 1.875rem; }
.mil-mb60 { margin-bottom: 3.75rem; }
.mil-mb90 { margin-bottom: 5.625rem; }

.mil-p-160-100 {
  padding-top: 10rem;
  padding-bottom: 6.25rem;
}

.mil-p-0-100 {
  padding-bottom: 6.25rem;
}

.mil-p-0-160 {
  padding-bottom: 10rem;
}

@layer utilities {
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after {
    inset: -1px;
  }
}

