import type { Item, Reservation, ActivityLog, Category, Notification } from "@shared/schema";

export type { Item, Reservation, ActivityLog, Category, Notification };

export interface CategoryWithCounts extends Omit<Category, 'subTypes'> {
  subTypes: string[];
  totalCount: number;
  availableCount: number;
  isCustom: boolean;
}

async function handleResponse<T>(response: Response): Promise<T> {
  if (!response.ok) {
    const error = await response.json().catch(() => ({ error: 'Request failed' }));
    throw new Error(error.error || 'Request failed');
  }
  return response.json();
}

export const api = {
  auth: {
    login: async (username: string, password: string) => {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
        credentials: 'include'
      });
      return handleResponse(response);
    },
    logout: async () => {
      const response = await fetch('/api/auth/logout', {
        method: 'POST',
        credentials: 'include'
      });
      return handleResponse(response);
    }
  },
  items: {
    getAll: (category?: string, isEquipment?: boolean) => {
      const params = new URLSearchParams();
      if (category) params.append('category', category);
      if (isEquipment !== undefined) params.append('isEquipment', String(isEquipment));
      const url = params.toString() ? `/api/items?${params.toString()}` : '/api/items';
      console.log('API request URL:', url);
      return fetch(url, {
        credentials: 'include'
      }).then(handleResponse);
    },
    create: async (item: any) => {
      const response = await fetch('/api/items', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(item),
        credentials: 'include'
      });
      return handleResponse(response);
    },
    update: async (id: string, item: any) => {
      const response = await fetch(`/api/items/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(item),
        credentials: 'include'
      });
      return handleResponse(response);
    },
    delete: async (id: string) => {
      const response = await fetch(`/api/items/${id}`, {
        method: 'DELETE',
        credentials: 'include'
      });
      return handleResponse(response);
    }
  },
  reservations: {
    getAll: async (): Promise<Reservation[]> => {
      const response = await fetch('/api/reservations', { credentials: 'include' });
      return handleResponse(response);
    },
    getByItem: async (itemId: string): Promise<Reservation[]> => {
      const response = await fetch(`/api/reservations/item/${itemId}`, { credentials: 'include' });
      return handleResponse(response);
    },
    create: async (reservation: any) => {
      const response = await fetch('/api/reservations', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(reservation),
        credentials: 'include'
      });
      return handleResponse(response);
    },
    update: async (id: string, reservation: any) => {
      const response = await fetch(`/api/reservations/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(reservation),
        credentials: 'include'
      });
      return handleResponse(response);
    }
  },
  activityLogs: {
    getAll: async (): Promise<ActivityLog[]> => {
      const response = await fetch('/api/activity-logs', { credentials: 'include' });
      return handleResponse(response);
    }
  },
  categories: {
    getAll: async (isEquipment?: boolean): Promise<CategoryWithCounts[]> => {
      const params = new URLSearchParams();
      if (isEquipment !== undefined) params.append('isEquipment', String(isEquipment));
      const url = params.toString() ? `/api/categories?${params.toString()}` : '/api/categories';
      const response = await fetch(url, { credentials: 'include' });
      return handleResponse(response);
    }
  },
  customCategories: {
    create: async (category: any) => {
      const response = await fetch('/api/custom-categories', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(category),
        credentials: 'include'
      });
      return handleResponse(response);
    },
    update: async (id: string, category: any) => {
      const response = await fetch(`/api/custom-categories/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(category),
        credentials: 'include'
      });
      return handleResponse(response);
    },
    delete: async (id: string) => {
      const response = await fetch(`/api/custom-categories/${id}`, {
        method: 'DELETE',
        credentials: 'include'
      });
      return handleResponse(response);
    }
  },
  notifications: {
    getAll: async (): Promise<Notification[]> => {
      const response = await fetch('/api/notifications', { credentials: 'include' });
      return handleResponse(response);
    },
    markAsRead: async (id: string) => {
      const response = await fetch(`/api/notifications/${id}/read`, {
        method: 'PATCH',
        credentials: 'include'
      });
      return handleResponse(response);
    },
    markAllAsRead: async () => {
      const response = await fetch('/api/notifications/read-all', {
        method: 'PATCH',
        credentials: 'include'
      });
      return handleResponse(response);
    },
    delete: async (id: string) => {
      const response = await fetch(`/api/notifications/${id}`, {
        method: 'DELETE',
        credentials: 'include'
      });
      return handleResponse(response);
    }
  },
  qrCodes: {
    getItemQRCode: async (itemId: string): Promise<{ qrCode: string; barcode: string; publicUrl: string }> => {
      const response = await fetch(`/api/items/${itemId}/qrcode`, { credentials: 'include' });
      return handleResponse(response);
    },
    getAllQRCodes: async (): Promise<Array<{ id: string; barcode: string; productName: string; qrCode: string; publicUrl: string }>> => {
      const response = await fetch('/api/qrcodes/all', { credentials: 'include' });
      return handleResponse(response);
    }
  },
  scan: {
    process: async (barcode: string) => {
      const response = await fetch('/api/scan', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ barcode }),
        credentials: 'include'
      });
      return handleResponse(response);
    }
  },
  users: {
    getAll: async (): Promise<any[]> => {
      const response = await fetch('/api/users', { credentials: 'include' });
      return handleResponse(response);
    },
    create: async (userData: any) => {
      const response = await fetch('/api/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData),
        credentials: 'include'
      });
      return handleResponse(response);
    },
    update: async (id: string, userData: any) => {
      const response = await fetch(`/api/users/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData),
        credentials: 'include'
      });
      return handleResponse(response);
    },
    delete: async (id: string) => {
      const response = await fetch(`/api/users/${id}`, {
        method: 'DELETE',
        credentials: 'include'
      });
      return handleResponse(response);
    }
  },
  damageReports: {
    getAll: async (): Promise<any[]> => {
      const response = await fetch('/api/damage-reports', { credentials: 'include' });
      return handleResponse(response);
    },
    create: async (report: any) => {
      const response = await fetch('/api/damage-reports', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(report),
        credentials: 'include'
      });
      return handleResponse(response);
    },
    resolve: async (id: string, notes: string) => {
      const response = await fetch(`/api/damage-reports/${id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: 'resolved', resolutionNotes: notes }),
        credentials: 'include'
      });
      return handleResponse(response);
    }
  }
};

client/src/pages/QRCodes.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Download, Copy, ExternalLink } from "lucide-react";
import { api } from "@/lib/api";
import InventoryHeader from "@/components/InventoryHeader";
import { useToast } from "@/hooks/use-toast";
import type { Language } from "@/lib/translations";
import { useTranslation } from "@/lib/translations";

interface QRCodesProps {
  userName: string;
  userRole: string;
  onLogout: () => void;
  onNavigateToInventory?: () => void;
  onNavigateToReservations?: () => void;
  onNavigateToActivityLogs?: () => void;
  onNavigateToMaintenance?: () => void;
  onNavigateToReports?: () => void;
  language?: Language;
  onLanguageChange?: (lang: Language) => void;
  currentLanguage?: Language;
}

export default function QRCodes({ userName, userRole, onLogout, onNavigateToInventory, onNavigateToReservations, onNavigateToActivityLogs, onNavigateToMaintenance, onNavigateToReports, language = 'en', onLanguageChange, currentLanguage = 'en' }: QRCodesProps) {
  const { toast } = useToast();
  const lang: Language = language || currentLanguage || 'en';
  const t = useTranslation(lang);
  const { data: qrCodes = [], isLoading } = useQuery({
    queryKey: ['/api/qrcodes/all'],
    queryFn: () => api.qrCodes.getAllQRCodes(),
  });

  const downloadQRCode = (qrCode: string, fileName: string) => {
    const link = document.createElement('a');
    link.href = qrCode;
    link.download = `${fileName}.png`;
    link.click();
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({ title: "URL copied to clipboard!" });
  };

  const downloadAllQRCodes = () => {
    qrCodes.forEach((item: any, index: number) => {
      setTimeout(() => {
        downloadQRCode(item.qrCode, `${item.barcode}-${item.productName}`);
      }, index * 100);
    });
  };

  return (
    <div className="min-h-screen bg-background">
      <InventoryHeader
        userName={userName}
        userRole={userRole}
        currentView="inventory"
        onViewChange={() => onNavigateToInventory?.()}
        onLogout={onLogout}
        onNavigateToReservations={onNavigateToReservations}
        onNavigateToActivityLogs={onNavigateToActivityLogs}
        onNavigateToQRCodes={() => {}}
        onNavigateToMaintenance={onNavigateToMaintenance}
        onNavigateToReports={onNavigateToReports}
        hideViewToggle={true}
        language={lang}
        onLanguageChange={onLanguageChange || (() => {})}
      />

      <main className="max-w-[1400px] mx-auto px-5 py-8">
        <div className="text-center mb-10 p-10 bg-gradient-to-r from-[#667eea] to-[#764ba2] rounded-2xl text-white">
          <h1 className="text-4xl font-extrabold mb-4" data-testid="text-qrcodes-title">
            {t('qrCodes')}
          </h1>
          <p className="text-lg opacity-90 max-w-2xl mx-auto" data-testid="text-qrcodes-subtitle">
            {t('scanQRCodes')}
          </p>
        </div>

        <div className="flex items-center justify-end mb-6">
          <div className="flex gap-2">
            <Button
              onClick={downloadAllQRCodes}
              variant="outline"
              disabled={qrCodes.length === 0}
              className="bg-gradient-to-r from-[#667eea] to-[#764ba2] text-white border-0"
            >
              <Download className="w-4 h-4 mr-2" />
              {t('downloadAll')}
            </Button>
          </div>
        </div>

        {isLoading ? (
          <div className="text-center py-12">
            <p className="text-muted-foreground">{t('generatingQRCodes')}</p>
          </div>
        ) : qrCodes.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-muted-foreground">{t('noItemsFound')}</p>
          </div>
        ) : (
          <div className="grid grid-cols-[repeat(auto-fill,minmax(300px,1fr))] gap-6">
            {qrCodes.map((item: any) => (
              <Card key={item.id}>
                <CardHeader>
                  <CardTitle className="text-lg">{item.productName}</CardTitle>
                  <p className="text-sm text-muted-foreground">{item.barcode}</p>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="bg-white p-4 rounded-lg border">
                    <img
                      src={item.qrCode}
                      alt={`QR Code for ${item.productName}`}
                      className="w-full h-auto"
                    />
                  </div>

                  {item.publicUrl && (
                    <div className="space-y-2">
                      <p className="text-xs font-semibold text-muted-foreground">Public Scan URL:</p>
                      <div className="flex gap-2">
                        <input
                          type="text"
                          value={item.publicUrl}
                          readOnly
                          className="flex-1 px-3 py-2 text-xs bg-muted rounded border text-muted-foreground"
                        />
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => copyToClipboard(item.publicUrl)}
                        >
                          <Copy className="w-3 h-3" />
                        </Button>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => window.open(item.publicUrl, '_blank')}
                        >
                          <ExternalLink className="w-3 h-3" />
                        </Button>
                      </div>
                    </div>
                  )}

                  <Button
                    onClick={() => downloadQRCode(item.qrCode, `${item.barcode}-${item.productName}`)}
                    variant="outline"
                    className="w-full"
                  >
                    <Download className="w-4 h-4 mr-2" />
                    {t('downloadQRCode')}
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

server/emailService.ts.content:
import nodemailer from 'nodemailer';

// Email service configuration
const transporter = nodemailer.createTransport({
  host: process.env.SMTP_HOST || 'localhost',
  port: parseInt(process.env.SMTP_PORT || '587'),
  secure: process.env.SMTP_SECURE === 'true',
  auth: process.env.SMTP_USER && process.env.SMTP_PASS ? {
    user: process.env.SMTP_USER,
    pass: process.env.SMTP_PASS,
  } : undefined,
});

export interface EmailOptions {
  to: string;
  subject: string;
  html: string;
}

async function sendEmail(options: EmailOptions): Promise<boolean> {
  try {
    // If no SMTP configured, log the email instead of failing
    if (!process.env.SMTP_HOST) {
      console.log('📧 Email would be sent to:', options.to);
      console.log('   Subject:', options.subject);
      console.log('   Body:', options.html.substring(0, 100) + '...');
      return true;
    }

    await transporter.sendMail({
      from: process.env.SMTP_FROM || 'noreply@inventory.local',
      ...options,
    });
    console.log('✅ Email sent to:', options.to);
    return true;
  } catch (error) {
    console.error('❌ Error sending email:', error);
    return false;
  }
}

export async function sendReservationRequestEmail(
  adminEmail: string,
  data: {
    itemName: string;
    userName: string;
    userEmail: string;
    startDate: string;
    returnDate: string;
    notes?: string;
    deliveryRequired?: string;
    deliveryLocation?: string;
    deliveryStreet?: string;
    deliveryArea?: string;
    googleMapLink?: string;
  }
): Promise<boolean> {
  const html = `
    <!DOCTYPE html>
    <html lang="en">
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <style>
          * { margin: 0; padding: 0; box-sizing: border-box; }
          body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; color: #333; background: white; padding: 0; line-height: 1.6; }
          .container { padding: 40px 60px; text-align: left; }
          .header { text-align: center; border-bottom: 2px solid #ecf0f1; padding-bottom: 25px; margin-bottom: 25px; }
          .title { font-size: 20px; font-weight: 600; margin-bottom: 5px; color: #2c3e50; }
          .subtitle { font-size: 13px; color: #7f8c8d; }
          .body { text-align: left; }
          .details { margin: 25px 0; }
          .detail-row { display: flex; margin-bottom: 12px; }
          .detail-label { font-weight: 600; color: #555; min-width: 120px; }
          .detail-value { color: #333; flex: 1; }
          .detail-value a { color: #3498db; text-decoration: none; }
          .section { margin-top: 25px; }
          .section-title { font-weight: 600; color: #2c3e50; margin-bottom: 12px; font-size: 13px; text-transform: uppercase; letter-spacing: 0.5px; }
          .footer { font-size: 12px; color: #95a5a6; margin-top: 30px; padding-top: 20px; border-top: 1px solid #ecf0f1; }
        </style>
      </head>
      <body>
        <div class="container">
          <div class="header">
            <div class="title">📋 New Reservation Request</div>
            <div class="subtitle">${data.itemName}</div>
          </div>

          <div class="body">
            <p style="color: #555; margin-bottom: 20px;">A new reservation request has been submitted and is pending your approval.</p>

            <div class="section">
              <div class="section-title">Request Details</div>
              <div class="details">
                <div class="detail-row">
                  <div class="detail-label">Equipment:</div>
                  <div class="detail-value">${data.itemName}</div>
                </div>
                <div class="detail-row">
                  <div class="detail-label">Requester:</div>
                  <div class="detail-value">${data.userName}</div>
                </div>
                <div class="detail-row">
                  <div class="detail-label">Email:</div>
                  <div class="detail-value"><a href="mailto:${data.userEmail}">${data.userEmail}</a></div>
                </div>
                <div class="detail-row">
                  <div class="detail-label">Pickup Date:</div>
                  <div class="detail-value">${data.startDate}</div>
                </div>
                <div class="detail-row">
                  <div class="detail-label">Return Date:</div>
                  <div class="detail-value">${data.returnDate}</div>
                </div>
                ${data.notes ? `
                <div class="detail-row">
                  <div class="detail-label">Purpose:</div>
                  <div class="detail-value">${data.notes}</div>
                </div>
                ` : ''}
                ${data.deliveryRequired === 'yes' ? `
                <div class="detail-row">
                  <div style="font-size: 16px; font-weight: 700; background-color: #e3f2fd; padding: 10px 12px; border-radius: 4px; color: #1565c0;">🚚 Delivery Required:</div>
                </div>
                <div class="detail-row">
                  <div class="detail-label">Location:</div>
                  <div class="detail-value">${data.deliveryLocation || 'Not specified'}</div>
                </div>
                <div class="detail-row">
                  <div class="detail-label">Street:</div>
                  <div class="detail-value">${data.deliveryStreet || 'Not specified'}</div>
                </div>
                <div class="detail-row">
                  <div class="detail-label">Area:</div>
                  <div class="detail-value">${data.deliveryArea || 'Not specified'}</div>
                </div>
                ${data.googleMapLink ? `
                <div class="detail-row">
                  <div class="detail-label">Map:</div>
