import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { CATEGORIES, EQUIPMENT_CATEGORIES, ITEM_STATUSES } from "@shared/schema";
import { api } from "@/lib/api";
import { Plus } from "lucide-react";
import { useTranslation } from "@/lib/translations";
import type { Language } from "@/lib/translations";

interface ItemFormDialogProps {
  open: boolean;
  onClose: () => void;
  onSubmit: (item: any) => void;
  item?: {
    id?: string;
    barcode: string;
    productName: string;
    productNameAr?: string;
    productType: string;
    productTypeAr?: string;
    status: string;
    location?: string;
    notes?: string;
    isEquipment?: boolean;
    maintenanceAvailableDate?: string;
  };
  mode: 'add' | 'edit';
  userRole?: string;
  isEquipment?: boolean;
  language?: Language;
}

export default function ItemFormDialog({ open, onClose, onSubmit, item, mode, userRole, isEquipment = true, language = 'en' }: ItemFormDialogProps) {
  const t = useTranslation(language);
  const [formData, setFormData] = useState({
    barcode: item?.barcode || '',
    productName: item?.productName || '',
    productNameAr: item?.productNameAr || '',
    productType: item?.productType || '',
    productTypeAr: item?.productTypeAr || '',
    status: item?.status || 'Available',
    location: item?.location || '',
    notes: item?.notes || '',
    isEquipment: item?.isEquipment !== undefined ? item.isEquipment : isEquipment,
    maintenanceAvailableDate: item?.maintenanceAvailableDate || ''
  });

  const [showAddType, setShowAddType] = useState(false);
  const [newTypeName, setNewTypeName] = useState('');

  // Fetch categories based on item type to get custom subTypes
  const { data: categories = [] } = useQuery({
    queryKey: ['/api/categories', isEquipment],
    queryFn: () => api.categories.getAll(isEquipment),
    enabled: open
  });

  // Get all subTypes ONLY from the API-returned categories (which are already filtered by isEquipment)
  // This ensures complete separation between Equipment and Assets
  const allSubTypes = categories
    .flatMap(cat => cat.subTypes);

  const uniqueSubTypes = Array.from(new Set(allSubTypes)).sort();

  console.log(`✅ ItemFormDialog (isEquipment=${isEquipment}):`, {
    categoriesCount: categories.length,
    subTypesCount: uniqueSubTypes.length,
    subTypes: uniqueSubTypes
  });

  useEffect(() => {
    if (item) {
      setFormData({
        barcode: item.barcode,
        productName: item.productName,
        productNameAr: item.productNameAr || '',
        productType: item.productType,
        productTypeAr: item.productTypeAr || '',
        status: item.status,
        location: item.location || '',
        notes: item.notes || '',
        isEquipment: item.isEquipment !== undefined ? item.isEquipment : isEquipment,
        maintenanceAvailableDate: item.maintenanceAvailableDate || ''
      });
    } else {
      setFormData({
        barcode: '',
        productName: '',
        productNameAr: '',
        productType: '',
        productTypeAr: '',
        status: 'Available',
        location: '',
        notes: '',
        isEquipment: isEquipment,
        maintenanceAvailableDate: ''
      });
    }
  }, [item, open, isEquipment]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  const handleAddNewType = () => {
    if (newTypeName.trim()) {
      setFormData({ ...formData, productType: newTypeName.trim() });
      setNewTypeName('');
      setShowAddType(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl" data-testid={`dialog-${mode}-item`}>
        <DialogHeader>
          <DialogTitle>{mode === 'add' ? t('addItem') : t('editItem')}</DialogTitle>
        </DialogHeader>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="barcode">{t('barcode')} *</Label>
              <Input
                id="barcode"
                value={formData.barcode}
                onChange={(e) => setFormData({ ...formData, barcode: e.target.value })}
                placeholder="e.g., CAM-001-2024"
                required
                data-testid="input-barcode"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="productName">{t('productName')} (EN) *</Label>
              <Input
                id="productName"
                value={formData.productName}
                onChange={(e) => setFormData({ ...formData, productName: e.target.value })}
                placeholder="e.g., Canon EOS R5"
                required
                data-testid="input-product-name"
              />
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="productNameAr">{t('productName')} (AR)</Label>
              <Input
                id="productNameAr"
                value={formData.productNameAr}
                onChange={(e) => setFormData({ ...formData, productNameAr: e.target.value })}
                placeholder="e.g., كانون EOS R5"
                data-testid="input-product-name-ar"
                dir="rtl"
              />
            </div>
            <div className="space-y-2" />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="productType">{t('type')} (EN) *</Label>
              {mode === 'edit' ? (
                <div className="bg-muted p-2 rounded text-sm text-muted-foreground">
                  {formData.productType}
                </div>
              ) : !showAddType ? (
                <div className="space-y-2">
                  <Select
                    value={formData.productType}
                    onValueChange={(value) => {
                      if (value === '__add_new__') {
                        setShowAddType(true);
                      } else {
                        setFormData({ ...formData, productType: value });
                      }
                    }}
                  >
                    <SelectTrigger data-testid="select-product-type">
                      <SelectValue placeholder="Select type" />
                    </SelectTrigger>
                    <SelectContent>
                      {uniqueSubTypes.map((type) => (
                        <SelectItem key={type} value={type}>
                          {type}
                        </SelectItem>
                      ))}
                      {userRole === 'admin' && (
                        <SelectItem value="__add_new__" className="text-blue-600 font-medium">
                          <div className="flex items-center">
                            <Plus className="w-4 h-4 mr-2" />
                            Add New Type
                          </div>
                        </SelectItem>
                      )}
                    </SelectContent>
                  </Select>
                </div>
              ) : (
                <div className="flex gap-2">
                  <Input
                    value={newTypeName}
                    onChange={(e) => setNewTypeName(e.target.value)}
                    placeholder="Enter new product type"
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') {
                        e.preventDefault();
                        handleAddNewType();
                      }
                    }}
                    autoFocus
                  />
                  <Button
                    type="button"
                    onClick={handleAddNewType}
                    disabled={!newTypeName.trim()}
                    size="sm"
                  >
                    Add
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => {
                      setShowAddType(false);
                      setNewTypeName('');
                    }}
                    size="sm"
                  >
                    Cancel
                  </Button>
                </div>
              )}
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="productTypeAr">{t('type')} (AR)</Label>
              {mode === 'edit' ? (
                <div className="bg-muted p-2 rounded text-sm text-muted-foreground" dir="rtl">
                  {formData.productTypeAr || 'Not specified'}
                </div>
              ) : (
                <Input
                  id="productTypeAr"
                  value={formData.productTypeAr}
                  onChange={(e) => setFormData({ ...formData, productTypeAr: e.target.value })}
                  placeholder="e.g., كاميرا"
                  data-testid="input-product-type-ar"
                  dir="rtl"
                />
              )}
            </div>
            <div className="space-y-2">
              <Label htmlFor="status">{t('status')} *</Label>
              <Select
                value={formData.status}
                onValueChange={(value) => setFormData({ ...formData, status: value })}
              >
                <SelectTrigger data-testid="select-status">
                  <SelectValue placeholder="Select status" />
                </SelectTrigger>
                <SelectContent>
                  {ITEM_STATUSES.map((status) => (
                    <SelectItem key={status} value={status}>
                      {status}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          {formData.status === 'Maintenance' && (
            <div className="space-y-2">
              <Label htmlFor="maintenanceDate">{t('maintenanceAvailableDate')} *</Label>
              <Input
                id="maintenanceDate"
                type="date"
                value={formData.maintenanceAvailableDate}
                onChange={(e) => setFormData({ ...formData, maintenanceAvailableDate: e.target.value })}
                required={formData.status === 'Maintenance'}
                data-testid="input-maintenance-date"
              />
            </div>
          )}

          <div className="space-y-2">
            <Label htmlFor="location">{t('location_label')}</Label>
            <Input
              id="location"
              value={formData.location}
              onChange={(e) => setFormData({ ...formData, location: e.target.value })}
              placeholder="e.g., Studio A - Shelf 2"
              data-testid="input-location"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="notes">{t('notes')}</Label>
            <Textarea
              id="notes"
              value={formData.notes}
              onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
              placeholder="Additional notes about the item"
              rows={3}
              data-testid="textarea-notes"
            />
          </div>

          <DialogFooter>
            <Button type="button" variant="outline" onClick={onClose} data-testid="button-cancel">
              {t('cancel')}
            </Button>
            <Button 
              type="submit" 
              className="bg-gradient-to-r from-[#667eea] to-[#764ba2]"
              data-testid="button-submit"
              disabled={!formData.barcode || !formData.productName || !formData.productType || !formData.status}
            >
              {mode === 'add' ? t('save') : t('save')}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ReservationActionD.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { CheckCircle, AlertCircle } from "lucide-react";

interface ReservationActionDialogProps {
  open: boolean;
  onClose: () => void;
  onSubmit: (data: { rejectionReason?: string; itemConditionOnReturn?: string; returnCondition?: 'good' | 'damage' }) => void;
  action: 'reject' | 'complete';
  itemName: string;
}

export default function ReservationActionDialog({
  open,
  onClose,
  onSubmit,
  action,
  itemName
}: ReservationActionDialogProps) {
  const [reason, setReason] = useState("");
  const [returnCondition, setReturnCondition] = useState<'good' | 'damage' | ''>("");

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (action === 'reject') {
      onSubmit({ rejectionReason: reason.trim() || undefined });
    } else {
      if (!returnCondition) return;
      if (returnCondition === 'damage' && !reason.trim()) return;
      onSubmit({ 
        itemConditionOnReturn: reason.trim() || undefined,
        returnCondition 
      });
    }
    setReason("");
    setReturnCondition("");
    onClose();
  };

  if (action === 'complete') {
    return (
      <Dialog open={open} onOpenChange={onClose}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Mark Equipment as Returned</DialogTitle>
          </DialogHeader>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="bg-blue-50 dark:bg-blue-950 p-4 rounded-lg border border-blue-200 dark:border-blue-800">
              <p className="text-sm">
                Item: <strong>{itemName}</strong>
              </p>
            </div>

            <div className="space-y-3">
              <Label className="text-base font-semibold">Equipment Condition Upon Return *</Label>
              <div className="space-y-3">
                <div className="flex items-center gap-3 p-3 border rounded-lg cursor-pointer hover:bg-muted/50" onClick={() => setReturnCondition('good')}>
                  <input 
                    type="radio" 
                    name="return-condition" 
                    value="good"
                    checked={returnCondition === 'good'}
                    onChange={() => setReturnCondition('good')}
                    className="w-4 h-4"
                  />
                  <Label className="cursor-pointer font-medium flex items-center gap-2 mb-0">
                    <CheckCircle className="w-5 h-5 text-green-600" />
                    Good
                  </Label>
                </div>

                <div className="flex items-center gap-3 p-3 border rounded-lg cursor-pointer hover:bg-muted/50" onClick={() => setReturnCondition('damage')}>
                  <input 
                    type="radio" 
                    name="return-condition" 
                    value="damage"
                    checked={returnCondition === 'damage'}
                    onChange={() => setReturnCondition('damage')}
                    className="w-4 h-4"
                  />
                  <Label className="cursor-pointer font-medium flex items-center gap-2 mb-0">
                    <AlertCircle className="w-5 h-5 text-red-600" />
                    Damage or Missing
                  </Label>
                </div>
              </div>
            </div>

            {returnCondition === 'damage' && (
              <div className="space-y-2 p-4 bg-red-50 dark:bg-red-950 rounded-lg border border-red-200 dark:border-red-800">
                <Label htmlFor="damage-notes" className="text-base font-semibold">Please describe the damage or missing items *</Label>
                <Textarea
                  id="damage-notes"
                  value={reason}
                  onChange={(e) => setReason(e.target.value)}
                  placeholder="Describe what damage or items are missing..."
                  rows={3}
                  className="resize-none"
                />
              </div>
            )}

            <DialogFooter>
              <Button type="button" variant="outline" onClick={() => {
                setReason("");
                setReturnCondition("");
                onClose();
              }}>
                Cancel
              </Button>
              <Button 
                type="submit" 
                variant="default"
                disabled={!returnCondition || (returnCondition === 'damage' && !reason.trim())}
              >
                Mark as Returned
              </Button>
            </DialogFooter>
          </form>
        </DialogContent>
      </Dialog>
    );
  }

  // Rejection dialog (unchanged)
  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>Reject Reservation</DialogTitle>
        </DialogHeader>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <p className="text-sm text-muted-foreground">
              Item: <strong>{itemName}</strong>
            </p>
          </div>

          <div className="space-y-2">
            <Label htmlFor="reason">Rejection Reason *</Label>
            <Textarea
              id="reason"
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              placeholder="Please provide a reason for rejection..."
              rows={4}
              required
            />
          </div>

          <DialogFooter>
            <Button type="button" variant="outline" onClick={() => {
              setReason("");
              onClose();
            }}>
              Cancel
            </Button>
            <Button 
              type="submit" 
              variant="destructive"
              disabled={!reason.trim()}
            >
              Reject Reservation
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ui/toggle.tsx.content:
import * as React from "react"
import * as TogglePrimitive from "@radix-ui/react-toggle"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const toggleVariants = cva(
  "inline-flex items-center justify-center rounded-md text-sm font-medium ring-offset-background transition-colors hover:bg-muted hover:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 data-[state=on]:bg-accent data-[state=on]:text-accent-foreground [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0 gap-2",
  {
    variants: {
      variant: {
        default: "bg-transparent",
        outline:
          "border border-input bg-transparent hover:bg-accent hover:text-accent-foreground",
      },
      size: {
        default: "h-10 px-3 min-w-10",
        sm: "h-9 px-2.5 min-w-9",
        lg: "h-11 px-5 min-w-11",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

const Toggle = React.forwardRef<
  React.ElementRef<typeof TogglePrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof TogglePrimitive.Root> &
    VariantProps<typeof toggleVariants>
>(({ className, variant, size, ...props }, ref) => (
  <TogglePrimitive.Root
    ref={ref}
    className={cn(toggleVariants({ variant, size, className }))}
    {...props}
  />
))

Toggle.displayName = TogglePrimitive.Root.displayName

export { Toggle, toggleVariants }

client/src/components/CategorySettingsDi.content:
import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";

interface CategorySettingsDialogProps {
  open: boolean;
  onClose: () => void;
  onSave: (settings: { showQuantity: boolean; showLocation: boolean; showNotes: boolean }) => void;
  category?: any;
}

export default function CategorySettingsDialog({ open, onClose, onSave, category }: CategorySettingsDialogProps) {
  const [settings, setSettings] = useState({
    showQuantity: true,
    showLocation: true,
    showNotes: true
  });

  useEffect(() => {
    if (category) {
      setSettings({
        showQuantity: category.showQuantity ?? true,
        showLocation: category.showLocation ?? true,
        showNotes: category.showNotes ?? true
      });
    }
  }, [category, open]);

  const handleSave = () => {
    onSave(settings);
    onClose();
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent data-testid="dialog-category-settings">
        <DialogHeader>
          <DialogTitle>Column Visibility Settings</DialogTitle>
        </DialogHeader>
        <div className="space-y-4">
          <p className="text-sm text-muted-foreground">
            Choose which columns to display for items in the <strong>{category?.name}</strong> category
          </p>
          <div className="space-y-3">
            <div className="flex items-center gap-3">
              <Checkbox
                id="showQuantity"
                checked={settings.showQuantity}
                onCheckedChange={(checked) =>
                  setSettings({ ...settings, showQuantity: checked as boolean })
                }
                data-testid="checkbox-show-quantity"
              />
              <Label htmlFor="showQuantity" className="cursor-pointer">
                Show Quantity Column
              </Label>
            </div>
            <div className="flex items-center gap-3">
              <Checkbox
                id="showLocation"
                checked={settings.showLocation}
                onCheckedChange={(checked) =>
                  setSettings({ ...settings, showLocation: checked as boolean })
                }
                data-testid="checkbox-show-location"
              />
              <Label htmlFor="showLocation" className="cursor-pointer">
                Show Location Column
              </Label>
            </div>
            <div className="flex items-center gap-3">
              <Checkbox
                id="showNotes"
                checked={settings.showNotes}
                onCheckedChange={(checked) =>
                  setSettings({ ...settings, showNotes: checked as boolean })
                }
                data-testid="checkbox-show-notes"
              />
              <Label htmlFor="showNotes" className="cursor-pointer">
                Show Notes Column
              </Label>
            </div>
          </div>
        </div>
        <DialogFooter>
          <Button variant="outline" onClick={onClose} data-testid="button-cancel-settings">
            Cancel
          </Button>
          <Button onClick={handleSave} data-testid="button-save-settings">
            Save Settings
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

